$color-bar-text: red;
$cirlce-size: 100px;

.stat-circle {
  width:$cirlce-size;
  transform:rotate(-90deg);

  circle.bg {
    fill: none;
    stroke: grey;
    stroke-width:.8;

  }
  circle.progress {
    fill: none;
    stroke: $color-bar-text;
    stroke-width: 0.8;
    stroke-dasharray: 50 50;
    stroke-dashoffset: -50;
    stroke-linecap: square;
  }
  text {
    font-size: 5px;
    font-weight: 600;
    text-anchor: middle;
    fill: $color-bar-text;
    transform-origin: 50% 50%;
    transform: rotate(90deg);

  }
}
